import React from 'react';
import { User, LogOut } from 'lucide-react';

const Navbar = ({ user, onLogout }) => {
  const styles = {
    container: {
      display: 'flex',
      justifyContent: 'space-between',
      alignItems: 'center',
      marginBottom: '2rem'
    },
    leftSection: {
      flex: 1
    },
    title: {
      fontSize: '2.25rem',
      fontWeight: 'bold',
      color: '#1f2937',
      marginBottom: '0.5rem'
    },
    subtitle: {
      color: '#6b7280'
    },
    rightSection: {
      display: 'flex',
      alignItems: 'center',
      gap: '1rem'
    },
    userCard: {
      display: 'flex',
      alignItems: 'center',
      gap: '0.5rem',
      background: 'white',
      padding: '0.5rem 1rem',
      borderRadius: '0.5rem',
      boxShadow: '0 1px 3px 0 rgba(0, 0, 0, 0.1)'
    },
    username: {
      fontWeight: '600',
      color: '#374151'
    },
    logoutButton: {
      background: '#ef4444',
      color: 'white',
