import React, { useState, useEffect, useRef } from 'react';
import { Plus, PlayCircle } from 'lucide-react';
import { animeAPI } from '../services/api';
import AnimeCard from '../components/AnimeCard';
import AddAnimeModal from '../components/AddAnimeModal';
import SeasonModal from '../components/SeasonModal';
import Navbar from '../components/Navbar';

const Dashboard = ({ user, onLogout }) => {
  const [animes, setAnimes] = useState([]);
  const [showAddModal, setShowAddModal] = useState(false);
  const [showSeasonModal, setShowSeasonModal] = useState(false);
  const [activeTab, setActiveTab] = useState('viendo');
  const [loading, setLoading] = useState(true);
  const [newAnime, setNewAnime] = useState({
    nombre: '',
    tipo: 'Shonen',
    capitulos_vistos: 0
  });

  useEffect(() => {
    loadAnimes();
  }, []);

  const loadAnimes = async () => {
    try {
      const data = await animeAPI.getAll();
      if (!data.error) {
        setAnimes(data);
      }
    } catch (err) {
      console.error('Error al cargar animes:', err);
    } finally {
      setLoading(false);
    }
  };

  const addAnime = async () => {
    if (!newAnime.nombre.trim()) return;
    
    try {
      const data = await animeAPI.create(newAnime);
      if (!data.error) {
        setAnimes([...animes, data]);
        setNewAnime({ nombre: '', tipo: 'Shonen', capitulos_vistos: 0 });
        setShowAddModal(false);
      }
    } catch (err) {
      console.error('Error al agregar anime:', err);
    }
  };

  const updateAnime = async (id, updatedData) => {
    try {
      const data = await animeAPI.update(id, updatedData);
      if (!data.error) {
        setAnimes(animes.map(a => a.id === id ? data : a));
      }
    } catch (err) {
      console.error('Error al actualizar anime:', err);
    }
  };

  const deleteAnime = async (id) => {
    try {
      await animeAPI.delete(id);
      setAnimes(animes.filter(a => a.id !== id));
    } catch (err) {
      console.error('Error al eliminar anime:', err);
    }
  };

  const addNewSeason = async (id) => {
    const anime = animes.find(a => a.id === id);
    if (anime) {
      await updateAnime(id, { 
        capitulos_vistos: anime.capitulos_vistos,
        estado: 'viendo', 
        calificacion: 0,
        nombre: anime.nombre
      });
      setShowSeasonModal(false);
    }
  };

  const filteredAnimes = animes.filter(a => a.estado === activeTab);

  const styles = {
    container: {
      minHeight: '100vh',
      background: 'linear-gradient(135deg, #e9d5ff 0%, #fce7f3 50%, #dbeafe 100%)'
    },
    content: {
      maxWidth: '72rem',
      margin: '0 auto',
      padding: '1.5rem'
    },
    loadingContainer: {
      minHeight: '100vh',
      background: 'linear-gradient(135deg, #e9d5ff 0%, #fce7f3 50%, #dbeafe 100%)',
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'center'
    },
    loadingText: {
      fontSize: '1.5rem',
      fontWeight: 'bold',
      color: '#9333ea'
    },
    tabContainer: {
      display: 'flex',
      gap: '0.5rem',
      marginBottom: '1.5rem',
      flexWrap: 'wrap'
    },
    tab: {
      padding: '0.75rem 1.5rem',
      borderRadius: '0.5rem',
      fontWeight: '600',
      border: 'none',
      cursor: 'pointer',
      transition: 'all 0.2s'
    },
    activeTab: {
      color: 'white',
      boxShadow: '0 10px 15px -3px rgba(0, 0, 0, 0.1)'
    },
    inactiveTab: {
      background: 'white',
      color: '#374151'
    },
    actionButtonsContainer: {
      marginBottom: '1.5rem',
      display: 'flex',
      gap: '0.5rem'
    },
    actionButton: {
      padding: '0.75rem 1.5rem',
      borderRadius: '0.5rem',
      border: 'none',
      color: 'white',
      cursor: 'pointer',
      display: 'flex',
      alignItems: 'center',
      gap: '0.5rem',
      fontWeight: '600',
      boxShadow: '0 10px 15px -3px rgba(0, 0, 0, 0.1)',
      transition: 'background-color 0.2s'
    },
    tableContainer: {
      background: 'white',
      borderRadius: '0.75rem',
      boxShadow: '0 4px 6px -1px rgba(0, 0, 0, 0.1)',
      overflow: 'hidden'
    },
    tableHeader: {
      display: 'grid',
      gridTemplateColumns: activeTab === 'viendo'
        ? '80px 1fr 150px 150px 120px 100px'
        : '80px 1fr 150px 150px 120px 140px 100px',
      gap: '1rem',
      padding: '1rem',
      background: '#f9fafb',
      borderBottom: '2px solid #e5e7eb',
      fontWeight: '600',
      color: '#374151',
      fontSize: '0.875rem'
    },
    emptyState: {
      textAlign: 'center',
      padding: '3rem',
      color: '#6b7280'
    }
  };

  if (loading) {
    return (
      <div style={styles.loadingContainer}>
        <div style={styles.loadingText}>Cargando...</div>
      </div>
    );
  }

  return (
    <div style={styles.container}>
      <div style={styles.content}>
        <Navbar user={user} onLogout={onLogout} />

        <div style={styles.tabContainer}>
          <button
            onClick={() => setActiveTab('viendo')}
            style={{
              ...styles.tab,
              ...(activeTab === 'viendo' 
                ? { ...styles.activeTab, background: '#9333ea' }
                : styles.inactiveTab)
            }}
            onMouseOver={(e) => {
              if (activeTab !== 'viendo') e.target.style.background = '#f9fafb';
            }}
            onMouseOut={(e) => {
              if (activeTab !== 'viendo') e.target.style.background = 'white';
            }}
          >
            üì∫ Viendo ({animes.filter(a => a.estado === 'viendo').length})
          </button>
          <button
            onClick={() => setActiveTab('completado')}
            style={{
              ...styles.tab,
              ...(activeTab === 'completado' 
                ? { ...styles.activeTab, background: '#16a34a' }
                : styles.inactiveTab)
            }}
            onMouseOver={(e) => {
              if (activeTab !== 'completado') e.target.style.background = '#f9fafb';
            }}
            onMouseOut={(e) => {
              if (activeTab !== 'completado') e.target.style.background = 'white';
            }}
          >
            ‚úÖ Completados ({animes.filter(a => a.estado === 'completado').length})
          </button>
          <button
            onClick={() => setActiveTab('abandonado')}
            style={{
              ...styles.tab,
              ...(activeTab === 'abandonado' 
                ? { ...styles.activeTab, background: '#ea580c' }
                : styles.inactiveTab)
            }}
            onMouseOver={(e) => {
              if (activeTab !== 'abandonado') e.target.style.background = '#f9fafb';
            }}
            onMouseOut={(e) => {
              if (activeTab !== 'abandonado') e.target.style.background = 'white';
            }}
          >
            üö´ Abandonados ({animes.filter(a => a.estado === 'abandonado').length})
          </button>
        </div>

        {activeTab === 'viendo' && (
          <div style={styles.actionButtonsContainer}>
            <button
              onClick={() => setShowAddModal(true)}
              style={{ ...styles.actionButton, background: '#9333ea' }}
              onMouseOver={(e) => e.target.style.background = '#7c3aed'}
              onMouseOut={(e) => e.target.style.background = '#9333ea'}
            >
              <Plus size={20} /> Nuevo Anime
            </button>
            <button
              onClick={() => setShowSeasonModal(true)}
              style={{ ...styles.actionButton, background: '#16a34a' }}
              onMouseOver={(e) => e.target.style.background = '#15803d'}
              onMouseOut={(e) => e.target.style.background = '#16a34a'}
            >
              <PlayCircle size={20} /> Nueva Temporada
            </button>
          </div>
        )}

        {/* Tabla de Animes */}
        <div style={styles.tableContainer}>
          {/* Header */}
          <div style={styles.tableHeader}>
            <div>Portada</div>
            <div>Nombre</div>
            <div>Tipo</div>
            <div>Cap√≠tulos</div>
            <div>B√∫squeda</div>
            {activeTab !== 'viendo' && <div>Calificaci√≥n</div>}
            <div>Acciones</div>
          </div>

          {/* Filas */}
          {filteredAnimes.length === 0 ? (
            <div style={styles.emptyState}>
              {activeTab === 'viendo' && '¬°Agrega tu primer anime!'}
              {activeTab === 'completado' && 'No has completado ning√∫n anime a√∫n'}
              {activeTab === 'abandonado' && 'No has abandonado ning√∫n anime'}
            </div>
          ) : (
            filteredAnimes.map(anime => (
              <AnimeCard
                key={anime.id}
                anime={anime}
                isWatching={activeTab === 'viendo'}
                onUpdate={updateAnime}
                onDelete={deleteAnime}
              />
            ))
          )}
        </div>

        <AddAnimeModal
          show={showAddModal}
          onClose={() => setShowAddModal(false)}
          onAdd={addAnime}
          formData={newAnime}
          setFormData={setNewAnime}
        />

        <SeasonModal
          show={showSeasonModal}
          onClose={() => setShowSeasonModal(false)}
          animes={animes}
          onSelect={addNewSeason}
        />
      </div>
    </div>
  );
};

export default Dashboard;
