import React, { useState } from 'react';
import { User, LogOut, Filter, TrendingUp, Award, Star, BarChart3 } from 'lucide-react';

const Navbar = ({ user, onLogout }) => {
  const styles = {
    container: {
      display: 'flex',
      justifyContent: 'space-between',
      alignItems: 'center',
      marginBottom: '2rem'
    },
    leftSection: {
      flex: 1
    },
    title: {
      fontSize: '2.25rem',
      fontWeight: 'bold',
      color: '#1f2937',
      marginBottom: '0.5rem'
    },
    subtitle: {
      color: '#6b7280'
    },
    rightSection: {
      display: 'flex',
      alignItems: 'center',
      gap: '1rem'
    },
    userCard: {
      display: 'flex',
      alignItems: 'center',
      gap: '0.5rem',
      background: 'white',
      padding: '0.5rem 1rem',
      borderRadius: '0.5rem',
      boxShadow: '0 1px 3px 0 rgba(0, 0, 0, 0.1)'
    },
    username: {
      fontWeight: '600',
      color: '#374151'
    },
    logoutButton: {
      background: '#ef4444',
      color: 'white',
      padding: '0.5rem 1rem',
      borderRadius: '0.5rem',
      border: 'none',
      cursor: 'pointer',
      display: 'flex',
      alignItems: 'center',
      gap: '0.5rem',
      fontWeight: '500',
      transition: 'background-color 0.2s'
    }
  };

  return (
    <div style={styles.container}>
      <div style={styles.leftSection}>
        <h1 style={styles.title}>ðŸŽŒ Mi Lista de Animes</h1>
        <p style={styles.subtitle}>Organiza y trackea todos tus animes favoritos</p>
      </div>
      <div style={styles.rightSection}>
        <div style={styles.userCard}>
          <User size={20} style={{ color: '#9333ea' }} />
          <span style={styles.username}>{user.username}</span>
        </div>
        <button
          onClick={onLogout}
          style={styles.logoutButton}
          onMouseOver={(e) => e.target.style.background = '#dc2626'}
          onMouseOut={(e) => e.target.style.background = '#ef4444'}
        >
          <LogOut size={20} /> Salir
        </button>
      </div>
    </div>
  );
};


export default Navbar;
