import React from 'react';

const AddAnimeModal = ({ show, onClose, onAdd, formData, setFormData }) => {
  if (!show) return null;

  const handleSubmit = (e) => {
    e.preventDefault();
    onAdd();
  };

  return (
    <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
      <div className="bg-white rounded-lg p-6 max-w-md w-full mx-4">
        <h2 className="text-2xl font-bold mb-4">Agregar Nuevo Anime</h2>
        
        <form onSubmit={handleSubmit} className="space-y-4">
          <div>
            <label className="block text-sm font-semibold mb-1">Nombre</label>
            <input
              type="text"
              value={formData.nombre}
              onChange={(e) => setFormData({...formData, nombre: e.target.value})}
              className="w-full border rounded-lg px-3 py-2"
              placeholder="Ej: Naruto Shippuden"
              required
            />
          </div>

          <div>
            <label className="block text-sm font-semibold mb-1">Tipo de Anime</label>
            <select
              value={formData.tipo}
              onChange={(e) => setFormData({...formData, tipo: e.target.value})}
              className="w-full border rounded-lg px-3 py-2"
            >
              <option>Shonen</option>
              <option>Shojo</option>
              <option>Seinen</option>
              <option>Josei</option>
              <option>Kodomomuke</option>
              <option>BL</option>
              <option>Otro</option>
            </select>
          </div>

          <div>
            <label className="block text-sm font-semibold mb-1">Cap√≠tulos Vistos</label>
            <input
              type="number"
              value={formData.capitulos_vistos}
              onChange={(e) => setFormData({...formData, capitulos_vistos: parseInt(e.target.value) || 0})}
              className="w-full border rounded-lg px-3 py-2"
              min="0"
            />
          </div>

          <div className="flex gap-2 mt-6">
            <button
              type="button"
              onClick={onClose}
              className="flex-1 bg-gray-200 px-4 py-2 rounded-lg hover:bg-gray-300"
            >
              Cancelar
            </button>
            <button
              type="submit"
              className="flex-1 bg-purple-600 text-white px-4 py-2 rounded-lg hover:bg-purple-700"
            >
              Agregar
            </button>
          </div>
        </form>
      </div>
    </div>
  );
};

export default AddAnimeModal;
