import React from 'react';

const SeasonModal = ({ show, onClose, animes, onSelect }) => {
  if (!show) return null;

  const completedAnimes = animes.filter(a => a.estado === 'completado');

  return (
    <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
      <div className="bg-white rounded-lg p-6 max-w-md w-full mx-4 max-h-96 overflow-y-auto">
        <h2 className="text-2xl font-bold mb-4">Seleccionar Anime Completado</h2>
        
        {completedAnimes.length === 0 ? (
          <p className="text-gray-500 text-center py-8">
            No tienes animes completados a√∫n
          </p>
        ) : (
          <div className="space-y-2">
            {completedAnimes.map(anime => (
              <button
                key={anime.id}
                onClick={() => onSelect(anime.id)}
                className="w-full text-left p-3 border rounded-lg hover:bg-purple-50 hover:border-purple-300 transition-colors"
              >
                <div className="font-semibold">{anime.nombre}</div>
                <div className="text-sm text-gray-600">{anime.tipo}</div>
              </button>
            ))}
          </div>
        )}

        <button
          onClick={onClose}
          className="w-full mt-4 bg-gray-200 px-4 py-2 rounded-lg hover:bg-gray-300"
        >
          Cancelar
        </button>
      </div>
    </div>
  );
};

export default SeasonModal;
