import React, { useState } from 'react';
import { ChevronUp, ChevronDown, MoreVertical, Star, Trash2, Check, PlayCircle } from 'lucide-react';

const AnimeCard = ({ anime, isWatching, onUpdate, onDelete }) => {
  const [showMenu, setShowMenu] = useState(false);
  const [showRating, setShowRating] = useState(false);
  const [rating, setRating] = useState(0);

  const handleUpdateCapitulos = async (delta) => {
    const newCaps = Math.max(0, anime.capitulos_vistos + delta);
    await onUpdate(anime.id, { 
      capitulos_vistos: newCaps,
      estado: anime.estado,
      calificacion: anime.calificacion 
    });
  };

  const handleChangeEstado = async (nuevoEstado, calificacion = 0) => {
    await onUpdate(anime.id, { 
      capitulos_vistos: anime.capitulos_vistos,
      estado: nuevoEstado, 
      calificacion 
    });
  };

  const handleFinish = async () => {
    await handleChangeEstado('completado', rating);
    setShowRating(false);
  };

  return (
    <div className="bg-white rounded-lg shadow-md p-4 hover:shadow-lg transition-shadow">
      <div className="flex justify-between items-start mb-3">
        <div className="flex-1">
          <h3 className="text-lg font-bold text-gray-800">{anime.nombre}</h3>
          <span className="inline-block bg-purple-100 text-purple-700 text-xs px-2 py-1 rounded mt-1">
            {anime.tipo}
          </span>
        </div>
        
        {isWatching && (
          <div className="relative">
            <button 
              onClick={() => setShowMenu(!showMenu)}
              className="p-1 hover:bg-gray-100 rounded"
            >
              <MoreVertical size={20} className="text-gray-600" />
            </button>
            
            {showMenu && (
              <div className="absolute right-0 mt-1 bg-white border rounded-lg shadow-lg z-10 w-40">
                <button
                  onClick={() => {
                    setShowRating(true);
                    setShowMenu(false);
                  }}
                  className="w-full text-left px-4 py-2 hover:bg-gray-50 flex items-center gap-2"
                >
                  <Check size={16} /> Finalizado
                </button>
                <button
                  onClick={() => {
                    handleChangeEstado('abandonado');
                    setShowMenu(false);
                  }}
                  className="w-full text-left px-4 py-2 hover:bg-gray-50 flex items-center gap-2 text-orange-600"
                >
                  <Trash2 size={16} /> Abandonar
                </button>
              </div>
            )}
          </div>
        )}
      </div>

      {anime.url && (
        <a 
          href={`https://www.google.com/search?q=${encodeURIComponent(anime.nombre + ' anime')}`}
          target="_blank"
          rel="noopener noreferrer"
          className="text-blue-500 text-sm hover:underline block mb-3"
        >
          üîç Buscar en Google
        </a>
      )}

      {isWatching ? (
        <div className="flex items-center gap-3">
          <span className="text-sm text-gray-600">Cap√≠tulos:</span>
          <div className="flex items-center gap-2 bg-gray-100 rounded-lg px-2 py-1">
            <button 
              onClick={() => handleUpdateCapitulos(-1)}
              className="p-1 hover:bg-gray-200 rounded"
            >
              <ChevronDown size={18} />
            </button>
            <span className="font-bold text-lg min-w-[40px] text-center">
              {anime.capitulos_vistos}
            </span>
            <button 
              onClick={() => handleUpdateCapitulos(1)}
              className="p-1 hover:bg-gray-200 rounded"
            >
              <ChevronUp size={18} />
            </button>
          </div>
        </div>
      ) : (
        <div className="space-y-2">
          <div className="flex justify-between text-sm">
            <span className="text-gray-600">Cap√≠tulos vistos:</span>
            <span className="font-semibold">{anime.capitulos_vistos}</span>
          </div>
          
          {anime.estado === 'completado' && anime.calificacion > 0 && (
            <div className="flex items-center gap-2">
              <span className="text-sm text-gray-600">Calificaci√≥n:</span>
              <div className="flex gap-1">
                {[1,2,3,4,5].map(n => (
                  <Star 
                    key={n} 
                    size={16} 
                    className={n <= anime.calificacion ? 'fill-yellow-400 text-yellow-400' : 'text-gray-300'}
                  />
                ))}
              </div>
            </div>
          )}

          <div className="flex gap-2 mt-3">
            {anime.estado === 'completado' && (
              <button
                onClick={() => handleChangeEstado('viendo', 0)}
                className="flex-1 bg-green-500 text-white px-3 py-2 rounded-lg hover:bg-green-600 text-sm flex items-center justify-center gap-1"
              >
                <PlayCircle size={16} /> Nueva Temporada
              </button>
            )}
            {anime.estado === 'abandonado' && (
              <button
                onClick={() => handleChangeEstado('viendo')}
                className="flex-1 bg-blue-500 text-white px-3 py-2 rounded-lg hover:bg-blue-600 text-sm flex items-center justify-center gap-1"
              >
                <PlayCircle size={16} /> Retomar
              </button>
            )}
            <button
              onClick={() => onDelete(anime.id)}
              className="bg-red-500 text-white px-3 py-2 rounded-lg hover:bg-red-600 text-sm flex items-center gap-1"
            >
              <Trash2 size={16} /> Eliminar
            </button>
          </div>
        </div>
      )}

      {showRating && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
          <div className="bg-white rounded-lg p-6 max-w-sm w-full mx-4">
            <h3 className="text-lg font-bold mb-4">Califica "{anime.nombre}"</h3>
            <div className="flex justify-center gap-2 mb-6">
              {[1,2,3,4,5].map(n => (
                <button
                  key={n}
                  onClick={() => setRating(n)}
                  className="transition-transform hover:scale-110"
                >
                  <Star 
                    size={32} 
                    className={n <= rating ? 'fill-yellow-400 text-yellow-400' : 'text-gray-300'}
                  />
                </button>
              ))}
            </div>
            <div className="flex gap-2">
              <button
                onClick={() => setShowRating(false)}
                className="flex-1 bg-gray-200 px-4 py-2 rounded-lg hover:bg-gray-300"
              >
                Cancelar
              </button>
              <button
                onClick={handleFinish}
                className="flex-1 bg-purple-600 text-white px-4 py-2 rounded-lg hover:bg-purple-700"
              >
                Finalizar
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default AnimeCard;
