import React, { useState } from 'react';
import { User, LogOut, Filter, TrendingUp, Award, Star, BarChart3 } from 'lucide-react';

const Navbar = ({ user, onLogout }) => {
  const styles = {
    container: {
      display: 'flex',
      justifyContent: 'space-between',
      alignItems: 'center',
      marginBottom: '2rem'
    },
    leftSection: {
      flex: 1
    },
    title: {
      fontSize: '2.25rem',
      fontWeight: 'bold',
      color: '#1f2937',
      marginBottom: '0.5rem'
    },
    subtitle: {
      color: '#6b7280'
    },
    rightSection: {
      display: 'flex',
      alignItems: 'center',
      gap: '1rem'
    },
    userCard: {
      display: 'flex',
      alignItems: 'center',
      gap: '0.5rem',
      background: 'white',
      padding: '0.5rem 1rem',
      borderRadius: '0.5rem',
      boxShadow: '0 1px 3px 0 rgba(0, 0, 0, 0.1)'
    },
    username: {
      fontWeight: '600',
      color: '#374151'
    },
    logoutButton: {
      background: '#ef4444',
      color: 'white',
      padding: '0.5rem 1rem',
      borderRadius: '0.5rem',
      border: 'none',
      cursor: 'pointer',
      display: 'flex',
      alignItems: 'center',
      gap: '0.5rem',
      fontWeight: '500',
      transition: 'background-color 0.2s'
    }
  };

  return (
    <div style={styles.container}>
      <div style={styles.leftSection}>
        <h1 style={styles.title}>üéå Mi Lista de Animes</h1>
        <p style={styles.subtitle}>Organiza y trackea todos tus animes favoritos</p>
      </div>
      <div style={styles.rightSection}>
        <div style={styles.userCard}>
          <User size={20} style={{ color: '#9333ea' }} />
          <span style={styles.username}>{user.username}</span>
        </div>
        <button
          onClick={onLogout}
          style={styles.logoutButton}
          onMouseOver={(e) => e.target.style.background = '#dc2626'}
          onMouseOut={(e) => e.target.style.background = '#ef4444'}
        >
          <LogOut size={20} /> Salir
        </button>
      </div>
    </div>
  );
};


// ============================================
// COMPONENTE STATISTICS
// ============================================
const Statistics = ({ animes }) => {
  const totalEpisodes = animes.reduce((sum, a) => sum + (a.capitulos_vistos || 0), 0);
  const completedAnimes = animes.filter(a => a.estado === 'completado').length;
  const watchingAnimes = animes.filter(a => a.estado === 'viendo').length;
  
  const typeStats = animes.reduce((acc, anime) => {
    acc[anime.tipo] = (acc[anime.tipo] || 0) + 1;
    return acc;
  }, {});

  const topTypes = Object.entries(typeStats)
    .sort((a, b) => b[1] - a[1])
    .slice(0, 5);

  const avgRating = animes
    .filter(a => a.calificacion > 0)
    .reduce((sum, a, _, arr) => sum + a.calificacion / arr.length, 0)
    .toFixed(1);

  const styles = {
    container: {
      display: 'grid',
      gridTemplateColumns: 'repeat(auto-fit, minmax(250px, 1fr))',
      gap: '1.5rem',
      marginBottom: '2rem'
    },
    card: {
      background: 'white',
      borderRadius: '0.75rem',
      padding: '1.5rem',
      boxShadow: '0 4px 6px -1px rgba(0, 0, 0, 0.1)'
    },
    cardHeader: {
      display: 'flex',
      alignItems: 'center',
      gap: '0.75rem',
      marginBottom: '1rem'
    },
    cardTitle: {
      fontSize: '1rem',
      fontWeight: '600',
      color: '#374151'
    },
    statNumber: {
      fontSize: '2.5rem',
      fontWeight: 'bold',
      color: '#9333ea',
      lineHeight: 1
    },
    statLabel: {
      fontSize: '0.875rem',
      color: '#6b7280',
      marginTop: '0.5rem'
    },
    typeList: {
      display: 'flex',
      flexDirection: 'column',
      gap: '0.75rem'
    },
    typeItem: {
      display: 'flex',
      justifyContent: 'space-between',
      alignItems: 'center'
    },
    typeName: {
      fontSize: '0.875rem',
      color: '#374151'
    },
    typeCount: {
      background: '#f3e8ff',
      color: '#7c3aed',
      padding: '0.25rem 0.75rem',
      borderRadius: '1rem',
      fontSize: '0.875rem',
      fontWeight: '600'
    }
  };

  return (
    <div style={styles.container}>
      <div style={styles.card}>
        <div style={styles.cardHeader}>
          <TrendingUp size={24} style={{ color: '#9333ea' }} />
          <span style={styles.cardTitle}>Cap√≠tulos Totales</span>
        </div>
        <div style={styles.statNumber}>{totalEpisodes}</div>
        <div style={styles.statLabel}>episodios vistos</div>
      </div>

      <div style={styles.card}>
        <div style={styles.cardHeader}>
          <Award size={24} style={{ color: '#16a34a' }} />
          <span style={styles.cardTitle}>Animes Completados</span>
        </div>
        <div style={styles.statNumber}>{completedAnimes}</div>
        <div style={styles.statLabel}>de {animes.length} totales</div>
      </div>

      <div style={styles.card}>
        <div style={styles.cardHeader}>
          <Star size={24} style={{ color: '#facc15' }} />
          <span style={styles.cardTitle}>Calificaci√≥n Promedio</span>
        </div>
        <div style={styles.statNumber}>{avgRating || '0.0'}</div>
        <div style={styles.statLabel}>‚≠ê de 5 estrellas</div>
      </div>

      <div style={styles.card}>
        <div style={styles.cardHeader}>
          <BarChart3 size={24} style={{ color: '#3b82f6' }} />
          <span style={styles.cardTitle}>G√©neros Favoritos</span>
        </div>
        <div style={styles.typeList}>
          {topTypes.length > 0 ? topTypes.map(([type, count]) => (
            <div key={type} style={styles.typeItem}>
              <span style={styles.typeName}>{type}</span>
              <span style={styles.typeCount}>{count}</span>
            </div>
          )) : (
            <span style={{ color: '#9ca3af', fontSize: '0.875rem' }}>
              Sin datos a√∫n
            </span>
          )}
        </div>
      </div>
    </div>
  );
};

export default Navbar;
