import React, { useState } from 'react';
import { Filter } from 'lucide-react';

// ============================================
// COMPONENTE FILTERS
// ============================================
const Filters = ({ filters, setFilters, animeTypes }) => {
  const [showFilters, setShowFilters] = useState(false);

  const styles = {
    container: {
      background: 'white',
      borderRadius: '0.75rem',
      padding: '1rem',
      marginBottom: '1rem',
      boxShadow: '0 1px 3px 0 rgba(0, 0, 0, 0.1)'
    },
    header: {
      display: 'flex',
      justifyContent: 'space-between',
      alignItems: 'center',
      marginBottom: showFilters ? '1rem' : '0'
    },
    title: {
      display: 'flex',
      alignItems: 'center',
      gap: '0.5rem',
      fontWeight: '600',
      color: '#374151'
    },
    toggleButton: {
      background: '#f3f4f6',
      border: 'none',
      borderRadius: '0.375rem',
      padding: '0.5rem 1rem',
      cursor: 'pointer',
      fontSize: '0.875rem',
      fontWeight: '500',
      transition: 'background-color 0.2s'
    },
    filtersGrid: {
      display: 'grid',
      gridTemplateColumns: 'repeat(auto-fit, minmax(200px, 1fr))',
      gap: '1rem'
    },
    filterGroup: {
      display: 'flex',
      flexDirection: 'column',
      gap: '0.5rem'
    },
    label: {
      fontSize: '0.875rem',
      fontWeight: '600',
      color: '#374151'
    },
    select: {
      width: '100%',
      padding: '0.5rem',
      border: '1px solid #d1d5db',
      borderRadius: '0.375rem',
      fontSize: '0.875rem',
      background: 'white'
    },
    clearButton: {
      background: '#ef4444',
      color: 'white',
      border: 'none',
      borderRadius: '0.375rem',
      padding: '0.5rem 1rem',
      cursor: 'pointer',
      fontSize: '0.875rem',
      fontWeight: '500',
      transition: 'background-color 0.2s'
    }
  };

  return (
    <div style={styles.container}>
      <div style={styles.header}>
        <div style={styles.title}>
          <Filter size={20} />
          Filtros y Ordenamiento
        </div>
        <button
          onClick={() => setShowFilters(!showFilters)}
          style={styles.toggleButton}
          onMouseOver={(e) => e.target.style.background = '#e5e7eb'}
          onMouseOut={(e) => e.target.style.background = '#f3f4f6'}
        >
          {showFilters ? 'Ocultar' : 'Mostrar'}
        </button>
      </div>

      {showFilters && (
        <div style={styles.filtersGrid}>
          <div style={styles.filterGroup}>
            <label style={styles.label}>Tipo de Anime</label>
            <select
              value={filters.tipo}
              onChange={(e) => setFilters({ ...filters, tipo: e.target.value })}
              style={styles.select}
            >
              <option value="">Todos los tipos</option>
              {animeTypes.map(type => (
                <option key={type} value={type}>{type}</option>
              ))}
            </select>
          </div>

          <div style={styles.filterGroup}>
            <label style={styles.label}>Calificación</label>
            <select
              value={filters.rating}
              onChange={(e) => setFilters({ ...filters, rating: e.target.value })}
              style={styles.select}
            >
              <option value="">Todas las calificaciones</option>
              <option value="5">⭐⭐⭐⭐⭐ 5 estrellas</option>
              <option value="4">⭐⭐⭐⭐ 4+ estrellas</option>
              <option value="3">⭐⭐⭐ 3+ estrellas</option>
            </select>
          </div>

          <div style={styles.filterGroup}>
            <label style={styles.label}>Ordenar por</label>
            <select
              value={filters.sortBy}
              onChange={(e) => setFilters({ ...filters, sortBy: e.target.value })}
              style={styles.select}
            >
              <option value="updated">Última actualización</option>
              <option value="name-asc">Nombre (A-Z)</option>
              <option value="name-desc">Nombre (Z-A)</option>
              <option value="episodes-desc">Más capítulos</option>
              <option value="episodes-asc">Menos capítulos</option>
              <option value="rating-desc">Mayor calificación</option>
            </select>
          </div>

          <div style={{ display: 'flex', alignItems: 'flex-end' }}>
            <button
              onClick={() => setFilters({ tipo: '', rating: '', sortBy: 'updated' })}
              style={styles.clearButton}
              onMouseOver={(e) => e.target.style.background = '#dc2626'}
              onMouseOut={(e) => e.target.style.background = '#ef4444'}
            >
              Limpiar Filtros
            </button>
          </div>
        </div>
      )}
    </div>
  );
};

export default Filters;
