import React, { useState, useEffect } from 'react';
import { Plus, PlayCircle } from 'lucide-react';
import { animeAPI } from '../services/api';
import AnimeCard from '../components/AnimeCard';
import AddAnimeModal from '../components/AddAnimeModal';
import SeasonModal from '../components/SeasonModal';
import Navbar from '../components/Navbar';
import Filters from '../components/Filters';
import Statistics from '../components/Statistics';

const Dashboard = ({ user, onLogout }) => {
  const [animes, setAnimes] = useState([]);
  const [loading, setLoading] = useState(true);
  const [activeTab, setActiveTab] = useState('viendo');
  const [showStatistics, setShowStatistics] = useState(false);
  const [showAddModal, setShowAddModal] = useState(false);
  const [showSeasonModal, setShowSeasonModal] = useState(false);
  const [filters, setFilters] = useState({
    tipo: '',
    rating: '',
    sortBy: 'updated'
  });

  // Cargar animes del backend
  useEffect(() => {
    loadAnimes();
  }, []);

  const ANIME_TYPES = [
    'Shonen', 'Shojo', 'Seinen', 'Josei', 'Kodomomuke',
    'Isekai', 'Mecha', 'Slice of Life', 'Romance', 'Comedia',
    'Drama', 'Acci√≥n', 'Aventura', 'Fantas√≠a', 'Sci-Fi',
    'Horror', 'Misterio', 'Deportes', 'Musical', 'Ecchi',
    'Harem', 'Yaoi', 'Yuri', 'Supernatural', 'Otro'
  ];

  // Cargar animes desde la API
  const loadAnimes = async () => {
    try {
      // Esta funci√≥n debe estar en tu api.js
      const API_URL = process.env.REACT_APP_API_URL || 'http://localhost:3001/api';
      const token = localStorage.getItem('token');
      
      const res = await fetch(`${API_URL}/animes`, {
        headers: { 'Authorization': `Bearer ${token}` }
      });
      const data = await res.json();
      
      if (!data.error) {
        setAnimes(data);
      }
    } catch (err) {
      console.error('Error al cargar animes:', err);
    } finally {
      setLoading(false);
    }
  };

  const addAnime = async (formData) => {
    try {
      const API_URL = process.env.REACT_APP_API_URL || 'http://localhost:3001/api';
      const token = localStorage.getItem('token');
      
      const res = await fetch(`${API_URL}/animes`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${token}`
        },
        body: JSON.stringify(formData)
      });
      const data = await res.json();
      
      if (!data.error) {
        setAnimes([...animes, data]);
        setShowAddModal(false);
      }
    } catch (err) {
      console.error('Error al agregar anime:', err);
    }
  };

  const addNewSeason = async (id) => {
    const anime = animes.find(a => a.id === id);
    if (anime) {
      await updateAnime(id, { 
        capitulos_vistos: anime.capitulos_vistos,
        estado: 'viendo', 
        calificacion: 0,
        nombre: anime.nombre
      });
      setShowSeasonModal(false);
    }
  };

  const updateAnime = async (id, updatedData) => {
    try {
      const API_URL = process.env.REACT_APP_API_URL || 'http://localhost:3001/api';
      const token = localStorage.getItem('token');
      
      const res = await fetch(`${API_URL}/animes/${id}`, {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${token}`
        },
        body: JSON.stringify(updatedData)
      });
      const data = await res.json();
      
      if (!data.error) {
        setAnimes(animes.map(a => a.id === id ? data : a));
      }
    } catch (err) {
      console.error('Error al actualizar anime:', err);
    }
  };

  const deleteAnime = async (id) => {
    try {
      const API_URL = process.env.REACT_APP_API_URL || 'http://localhost:3001/api';
      const token = localStorage.getItem('token');
      
      await fetch(`${API_URL}/animes/${id}`, {
        method: 'DELETE',
        headers: { 'Authorization': `Bearer ${token}` }
      });
      
      setAnimes(animes.filter(a => a.id !== id));
    } catch (err) {
      console.error('Error al eliminar anime:', err);
    }
  };

  // Filtrar y ordenar animes
  const getFilteredAnimes = () => {
    let filtered = animes.filter(a => a.estado === activeTab);

    // Aplicar filtro de tipo
    if (filters.tipo) {
      filtered = filtered.filter(a => a.tipo === filters.tipo);
    }

    // Aplicar filtro de calificaci√≥n
    if (filters.rating) {
      const minRating = parseInt(filters.rating);
      filtered = filtered.filter(a => a.calificacion >= minRating);
    }

    // Aplicar ordenamiento
    switch (filters.sortBy) {
      case 'name-asc':
        filtered.sort((a, b) => a.nombre.localeCompare(b.nombre));
        break;
      case 'name-desc':
        filtered.sort((a, b) => b.nombre.localeCompare(a.nombre));
        break;
      case 'episodes-desc':
        filtered.sort((a, b) => b.capitulos_vistos - a.capitulos_vistos);
        break;
      case 'episodes-asc':
        filtered.sort((a, b) => a.capitulos_vistos - b.capitulos_vistos);
        break;
      case 'rating-desc':
        filtered.sort((a, b) => b.calificacion - a.calificacion);
        break;
      default:
        break;
    }

    return filtered;
  };

  const filteredAnimes = getFilteredAnimes();

  const styles = {
    container: {
      minHeight: '100vh',
      background: 'linear-gradient(135deg, #e9d5ff 0%, #fce7f3 50%, #dbeafe 100%)'
    },
    content: {
      maxWidth: '72rem',
      margin: '0 auto',
      padding: '1.5rem'
    },
    loadingContainer: {
      minHeight: '100vh',
      background: 'linear-gradient(135deg, #e9d5ff 0%, #fce7f3 50%, #dbeafe 100%)',
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'center'
    },
    loadingText: {
      fontSize: '1.5rem',
      fontWeight: 'bold',
      color: '#9333ea'
    },
    tabContainer: {
      display: 'flex',
      gap: '0.5rem',
      marginBottom: '1.5rem',
      flexWrap: 'wrap'
    },
    tab: {
      padding: '0.75rem 1.5rem',
      borderRadius: '0.5rem',
      fontWeight: '600',
      border: 'none',
      cursor: 'pointer',
      transition: 'all 0.2s'
    },
    activeTab: {
      color: 'white',
      boxShadow: '0 10px 15px -3px rgba(0, 0, 0, 0.1)'
    },
    inactiveTab: {
      background: 'white',
      color: '#374151'
    },
    actionButtonsContainer: {
      marginBottom: '1.5rem',
      display: 'flex',
      gap: '0.5rem'
    },
    actionButton: {
      padding: '0.75rem 1.5rem',
      borderRadius: '0.5rem',
      border: 'none',
      color: 'white',
      cursor: 'pointer',
      display: 'flex',
      alignItems: 'center',
      gap: '0.5rem',
      fontWeight: '600',
      boxShadow: '0 10px 15px -3px rgba(0, 0, 0, 0.1)',
      transition: 'background-color 0.2s'
    },
    tableContainer: {
      background: 'white',
      borderRadius: '0.75rem',
      boxShadow: '0 4px 6px -1px rgba(0, 0, 0, 0.1)',
      overflow: 'hidden'
    },
    tableHeader: {
      display: 'grid',
      gridTemplateColumns: activeTab === 'viendo'
        ? '80px 1fr 150px 150px 120px 100px'
        : '80px 1fr 150px 150px 120px 140px 100px',
      gap: '1rem',
      padding: '1rem',
      background: '#f9fafb',
      borderBottom: '2px solid #e5e7eb',
      fontWeight: '600',
      color: '#374151',
      fontSize: '0.875rem'
    },
    emptyState: {
      textAlign: 'center',
      padding: '3rem',
      color: '#6b7280'
    }
  };

  // Mostrar loading mientras carga
  if (loading) {
    return (
      <div style={styles.loadingContainer}>
        <div style={styles.loadingText}>Cargando...</div>
      </div>
    );
  }

  return (
    <div style={styles.container}>
      <div style={styles.content}>
        <Navbar user={user} onLogout={onLogout} />

        {/* Tabs */}
        <div style={styles.tabContainer}>
          <button
            onClick={() => { setActiveTab('viendo'); setShowStatistics(false); }}
            style={{
              ...styles.tab,
              ...(activeTab === 'viendo' && !showStatistics
                ? { ...styles.activeTab, background: '#9333ea' }
                : styles.inactiveTab)
            }}
            onMouseOver={(e) => {
              if (activeTab !== 'viendo' || showStatistics) e.target.style.background = '#f9fafb';
            }}
            onMouseOut={(e) => {
              if (activeTab !== 'viendo' || showStatistics) e.target.style.background = 'white';
            }}
          >
            üì∫ Viendo ({animes.filter(a => a.estado === 'viendo').length})
          </button>
          <button
            onClick={() => { setActiveTab('completado'); setShowStatistics(false); }}
            style={{
              ...styles.tab,
              ...(activeTab === 'completado' && !showStatistics
                ? { ...styles.activeTab, background: '#16a34a' }
                : styles.inactiveTab)
            }}
            onMouseOver={(e) => {
              if (activeTab !== 'completado' || showStatistics) e.target.style.background = '#f9fafb';
            }}
            onMouseOut={(e) => {
              if (activeTab !== 'completado' || showStatistics) e.target.style.background = 'white';
            }}
          >
            ‚úÖ Completados ({animes.filter(a => a.estado === 'completado').length})
          </button>
          <button
            onClick={() => { setActiveTab('abandonado'); setShowStatistics(false); }}
            style={{
              ...styles.tab,
              ...(activeTab === 'abandonado' && !showStatistics
                ? { ...styles.activeTab, background: '#ea580c' }
                : styles.inactiveTab)
            }}
            onMouseOver={(e) => {
              if (activeTab !== 'abandonado' || showStatistics) e.target.style.background = '#f9fafb';
            }}
            onMouseOut={(e) => {
              if (activeTab !== 'abandonado' || showStatistics) e.target.style.background = 'white';
            }}
          >
            üö´ Abandonados ({animes.filter(a => a.estado === 'abandonado').length})
          </button>
          <button
            onClick={() => setShowStatistics(true)}
            style={{
              ...styles.tab,
              ...(showStatistics
                ? { ...styles.activeTab, background: '#3b82f6' }
                : styles.inactiveTab)
            }}
            onMouseOver={(e) => {
              if (!showStatistics) e.target.style.background = '#f9fafb';
            }}
            onMouseOut={(e) => {
              if (!showStatistics) e.target.style.background = 'white';
            }}
          >
            üìä Estad√≠sticas
          </button>
        </div>

        {/* Contenido */}
        {showStatistics ? (
          <Statistics animes={animes} />
        ) : (
          <>
            <Filters 
              filters={filters} 
              setFilters={setFilters} 
              animeTypes={ANIME_TYPES}
            />

            {/* Botones de acci√≥n - Solo en pesta√±a "Viendo" */}
            {activeTab === 'viendo' && (
              <div style={styles.actionButtonsContainer}>
                <button
                  onClick={() => setShowAddModal(true)}
                  style={{ ...styles.actionButton, background: '#9333ea' }}
                  onMouseOver={(e) => e.target.style.background = '#7c3aed'}
                  onMouseOut={(e) => e.target.style.background = '#9333ea'}
                >
                  <Plus size={20} /> Nuevo Anime
                </button>
                <button
                  onClick={() => setShowSeasonModal(true)}
                  style={{ ...styles.actionButton, background: '#16a34a' }}
                  onMouseOver={(e) => e.target.style.background = '#15803d'}
                  onMouseOut={(e) => e.target.style.background = '#16a34a'}
                >
                  <PlayCircle size={20} /> Nueva Temporada
                </button>
              </div>
            )}

            <div style={styles.tableContainer}>
              <div style={styles.tableHeader}>
                <div>Portada</div>
                <div>Nombre</div>
                <div>Tipo</div>
                <div>Cap√≠tulos</div>
                <div>B√∫squeda</div>
                {activeTab !== 'viendo' && <div>Calificaci√≥n</div>}
                <div>Acciones</div>
              </div>

              {filteredAnimes.length === 0 ? (
                <div style={styles.emptyState}>
                  {activeTab === 'viendo' && 'No hay animes en esta categor√≠a'}
                  {activeTab === 'completado' && 'No hay animes completados con estos filtros'}
                  {activeTab === 'abandonado' && 'No hay animes abandonados con estos filtros'}
                </div>
              ) : (
                filteredAnimes.map(anime => (
                  <AnimeCard
                    key={anime.id}
                    anime={anime}
                    isWatching={activeTab === 'viendo'}
                    onUpdate={updateAnime}
                    onDelete={deleteAnime}
                  />
                ))
              )}
            </div>
          </>
        )}

        {/* Modales */}
        <AddAnimeModal
          show={showAddModal}
          onClose={() => setShowAddModal(false)}
          onAdd={addAnime}
        />

        <SeasonModal
          show={showSeasonModal}
          onClose={() => setShowSeasonModal(false)}
          animes={animes}
          onSelect={addNewSeason}
        />
      </div>
    </div>
  );
};

export default Dashboard;
