import React, { useState } from 'react';
import { authAPI } from '../services/api';

const Register = ({ onRegister, onSwitchToLogin }) => {
  const [formData, setFormData] = useState({ 
    username: '', 
    email: '', 
    password: '', 
    confirmPassword: '' 
  });
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');

    if (formData.password !== formData.confirmPassword) {
      setError('Las contraseÃ±as no coinciden');
      return;
    }

    if (formData.password.length < 6) {
      setError('La contraseÃ±a debe tener al menos 6 caracteres');
      return;
    }

    setLoading(true);

    try {
      const { confirmPassword, ...dataToSend } = formData;
      const data = await authAPI.register(dataToSend);
      
      if (data.error) {
        setError(data.error);
      } else {
        localStorage.setItem('token', data.token);
        localStorage.setItem('user', JSON.stringify(data.user));
        onRegister(data.user);
      }
    } catch (err) {
      setError('Error al conectar con el servidor');
    } finally {
      setLoading(false);
    }
  };

  const styles = {
    container: {
      minHeight: '100vh',
      background: '#0f172a',
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'center',
      padding: '1rem'
    },
    card: {
      background: '#1f2937',
      borderRadius: '1rem',
      border: '1px solid #374151',
      boxShadow: '0 20px 25px -5px rgba(0, 0, 0, 0.5)',
      padding: '2.5rem',
      width: '100%',
      maxWidth: '28rem'
    },
    header: {
      textAlign: 'center',
      marginBottom: '2rem'
    },
    title: {
      fontSize: '2.5rem',
      fontWeight: '700',
      color: '#f9fafb',
      marginBottom: '0.5rem',
      letterSpacing: '-0.025em'
    },
    subtitle: {
      color: '#9ca3af',
      fontSize: '0.95rem'
    },
    form: {
      display: 'flex',
      flexDirection: 'column',
      gap: '1.25rem'
    },
    error: {
      backgroundColor: '#7f1d1d',
      border: '1px solid #991b1b',
      color: '#fca5a5',
      padding: '0.75rem 1rem',
      borderRadius: '0.5rem',
      fontSize: '0.875rem'
    },
    inputGroup: {
      display: 'flex',
      flexDirection: 'column',
      gap: '0.5rem'
    },
    label: {
      display: 'block',
      fontSize: '0.875rem',
      fontWeight: '600',
      color: '#e5e7eb'
    },
    input: {
      width: '100%',
      border: '1px solid #374151',
      borderRadius: '0.5rem',
      padding: '0.75rem 1rem',
      fontSize: '1rem',
      transition: 'border-color 0.2s',
      boxSizing: 'border-box',
      background: '#111827',
      color: '#f9fafb'
    },
    button: {
      width: '100%',
      backgroundColor: loading ? '#4b5563' : '#9333ea',
      color: 'white',
      padding: '0.875rem',
      borderRadius: '0.5rem',
      fontWeight: '600',
      border: 'none',
      cursor: loading ? 'not-allowed' : 'pointer',
      fontSize: '1rem',
      transition: 'background-color 0.2s',
      marginTop: '0.5rem'
    },
    footer: {
      marginTop: '1.5rem',
      textAlign: 'center',
      color: '#9ca3af',
      fontSize: '0.9rem'
    },
    link: {
      color: '#a78bfa',
      fontWeight: '600',
      background: 'none',
      border: 'none',
      cursor: 'pointer',
      textDecoration: 'none',
      transition: 'color 0.2s'
    }
  };

  return (
    <div style={styles.container}>
      <div style={styles.card}>
        <div style={styles.header}>
          <h1 style={styles.title}>ðŸŽŒ Anime Tracker</h1>
          <p style={styles.subtitle}>Crea tu cuenta</p>
        </div>

        <form onSubmit={handleSubmit} style={styles.form}>
          {error && (
            <div style={styles.error}>
              {error}
            </div>
          )}

          <div style={styles.inputGroup}>
            <label style={styles.label}>Nombre de usuario</label>
            <input
              type="text"
              value={formData.username}
              onChange={(e) => setFormData({...formData, username: e.target.value})}
              style={styles.input}
              placeholder="otaku123"
              required
              onFocus={(e) => e.target.style.borderColor = '#6366f1'}
              onBlur={(e) => e.target.style.borderColor = '#374151'}
            />
          </div>

          <div style={styles.inputGroup}>
            <label style={styles.label}>Email</label>
            <input
              type="email"
              value={formData.email}
              onChange={(e) => setFormData({...formData, email: e.target.value})}
              style={styles.input}
              placeholder="tu@email.com"
              required
              onFocus={(e) => e.target.style.borderColor = '#6366f1'}
              onBlur={(e) => e.target.style.borderColor = '#374151'}
            />
          </div>

          <div style={styles.inputGroup}>
            <label style={styles.label}>ContraseÃ±a</label>
            <input
              type="password"
              value={formData.password}
              onChange={(e) => setFormData({...formData, password: e.target.value})}
              style={styles.input}
              placeholder="â€¢â€¢â€¢â€¢â€¢â€¢â€¢â€¢"
              required
              onFocus={(e) => e.target.style.borderColor = '#6366f1'}
              onBlur={(e) => e.target.style.borderColor = '#374151'}
            />
          </div>

          <div style={styles.inputGroup}>
            <label style={styles.label}>Confirmar ContraseÃ±a</label>
            <input
              type="password"
              value={formData.confirmPassword}
              onChange={(e) => setFormData({...formData, confirmPassword: e.target.value})}
              style={styles.input}
              placeholder="â€¢â€¢â€¢â€¢â€¢â€¢â€¢â€¢"
              required
              onFocus={(e) => e.target.style.borderColor = '#6366f1'}
              onBlur={(e) => e.target.style.borderColor = '#374151'}
            />
          </div>

          <button
            type="submit"
            disabled={loading}
            style={styles.button}
            onMouseOver={(e) => {
              if (!loading) e.target.style.background = '#7c3aed';
            }}
            onMouseOut={(e) => {
              if (!loading) e.target.style.background = '#9333ea';
            }}
          >
            {loading ? 'Registrando...' : 'Crear Cuenta'}
          </button>
        </form>

        <div style={styles.footer}>
          <p>
            Â¿Ya tienes cuenta?{' '}
            <button
              onClick={onSwitchToLogin}
              style={styles.link}
              onMouseOver={(e) => e.target.style.color = '#c4b5fd'}
              onMouseOut={(e) => e.target.style.color = '#a78bfa'}
            >
              Inicia sesiÃ³n aquÃ­
            </button>
          </p>
        </div>
      </div>
    </div>
  );
};

export default Register;
