import React, { useState } from 'react';
import { ChevronUp, ChevronDown, MoreVertical, Star, Trash2, Check, PlayCircle, Edit2 } from 'lucide-react';

const AnimeCard = ({ anime, isWatching, onUpdate, onDelete }) => {
  const [showMenu, setShowMenu] = useState(false);
  const [showRating, setShowRating] = useState(false);
  const [rating, setRating] = useState(0);

  const handleUpdateCapitulos = async (delta) => {
    const newCaps = Math.max(0, anime.capitulos_vistos + delta);
    await onUpdate(anime.id, { 
      capitulos_vistos: newCaps,
      estado: anime.estado,
      calificacion: anime.calificacion 
    });
  };

  const handleChangeEstado = async (nuevoEstado, calificacion = 0) => {
    await onUpdate(anime.id, { 
      capitulos_vistos: anime.capitulos_vistos,
      estado: nuevoEstado, 
      calificacion 
    });
  };

  const handleFinish = async () => {
    await handleChangeEstado('completado', rating);
    setShowRating(false);
  };

  const styles = {
    card: {
      background: 'white',
      borderRadius: '0.5rem',
      boxShadow: '0 4px 6px -1px rgba(0, 0, 0, 0.1)',
      padding: '1rem',
      transition: 'box-shadow 0.2s'
    },
    header: {
      display: 'flex',
      justifyContent: 'space-between',
      alignItems: 'flex-start',
      marginBottom: '0.75rem'
    },
    headerLeft: {
      flex: 1
    },
    title: {
      fontSize: '1.125rem',
      fontWeight: 'bold',
      color: '#1f2937',
      marginBottom: '0.25rem'
    },
    tag: {
      display: 'inline-block',
      background: '#f3e8ff',
      color: '#7c3aed',
      fontSize: '0.75rem',
      padding: '0.25rem 0.5rem',
      borderRadius: '0.25rem'
    },
    menuButton: {
      padding: '0.25rem',
      border: 'none',
      background: 'transparent',
      cursor: 'pointer',
      borderRadius: '0.25rem',
      transition: 'background-color 0.2s'
    },
    menuDropdown: {
      position: 'absolute',
      right: 0,
      marginTop: '0.25rem',
      background: 'white',
      border: '1px solid #e5e7eb',
      borderRadius: '0.5rem',
      boxShadow: '0 10px 15px -3px rgba(0, 0, 0, 0.1)',
      zIndex: 10,
      width: '10rem'
    },
    menuItem: {
      width: '100%',
      textAlign: 'left',
      padding: '0.5rem 1rem',
      border: 'none',
      background: 'white',
      cursor: 'pointer',
      display: 'flex',
      alignItems: 'center',
      gap: '0.5rem',
      transition: 'background-color 0.2s'
    },
    searchLink: {
      color: '#3b82f6',
      fontSize: '0.875rem',
      textDecoration: 'none',
      display: 'block',
      marginBottom: '0.75rem'
    },
    capitulosContainer: {
      display: 'flex',
      alignItems: 'center',
      gap: '0.75rem'
    },
    capitulosLabel: {
      fontSize: '0.875rem',
      color: '#6b7280'
    },
    capitulosControls: {
      display: 'flex',
      alignItems: 'center',
      gap: '0.5rem',
      background: '#f3f4f6',
      borderRadius: '0.5rem',
      padding: '0.25rem 0.5rem'
    },
    capitulosButton: {
      padding: '0.25rem',
      border: 'none',
      background: 'transparent',
      cursor: 'pointer',
      borderRadius: '0.25rem',
      display: 'flex',
      alignItems: 'center',
      transition: 'background-color 0.2s'
    },
    capitulosNumber: {
      fontWeight: 'bold',
      fontSize: '1.125rem',
      minWidth: '40px',
      textAlign: 'center'
    },
    infoContainer: {
      display: 'flex',
      flexDirection: 'column',
      gap: '0.5rem'
    },
    infoRow: {
      display: 'flex',
      justifyContent: 'space-between',
      fontSize: '0.875rem'
    },
    infoLabel: {
      color: '#6b7280'
    },
    infoValue: {
      fontWeight: '600'
    },
    ratingContainer: {
      display: 'flex',
      alignItems: 'center',
      gap: '0.5rem'
    },
    starContainer: {
      display: 'flex',
      gap: '0.25rem'
    },
    buttonGroup: {
      display: 'flex',
      gap: '0.5rem',
      marginTop: '0.75rem'
    },
    actionButton: {
      padding: '0.5rem 0.75rem',
      borderRadius: '0.5rem',
      border: 'none',
      cursor: 'pointer',
      fontSize: '0.875rem',
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'center',
      gap: '0.25rem',
      fontWeight: '500',
      transition: 'background-color 0.2s'
    },
    overlay: {
      position: 'fixed',
      inset: 0,
      background: 'rgba(0, 0, 0, 0.5)',
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'center',
      zIndex: 50
    },
    modal: {
      background: 'white',
      borderRadius: '0.5rem',
      padding: '1.5rem',
      maxWidth: '24rem',
      width: '100%',
      margin: '0 1rem'
    },
    modalTitle: {
      fontSize: '1.125rem',
      fontWeight: 'bold',
      marginBottom: '1rem',
      color: '#1f2937'
    },
    starsContainer: {
      display: 'flex',
      justifyContent: 'center',
      gap: '0.5rem',
      marginBottom: '1.5rem'
    },
    starButton: {
      background: 'none',
      border: 'none',
      cursor: 'pointer',
      transition: 'transform 0.2s',
      padding: 0
    },
    modalButtons: {
      display: 'flex',
      gap: '0.5rem'
    }
  };

  return (
    <div 
      style={styles.card}
      onMouseOver={(e) => e.currentTarget.style.boxShadow = '0 10px 15px -3px rgba(0, 0, 0, 0.1)'}
      onMouseOut={(e) => e.currentTarget.style.boxShadow = '0 4px 6px -1px rgba(0, 0, 0, 0.1)'}
    >
      <div style={styles.header}>
        <div style={styles.headerLeft}>
          <h3 style={styles.title}>{anime.nombre}</h3>
          <span style={styles.tag}>{anime.tipo}</span>
        </div>
        
        {isWatching && (
          <div style={{ position: 'relative' }}>
            <button 
              onClick={() => setShowMenu(!showMenu)}
              style={styles.menuButton}
              onMouseOver={(e) => e.target.style.background = '#f3f4f6'}
              onMouseOut={(e) => e.target.style.background = 'transparent'}
            >
              <MoreVertical size={20} style={{ color: '#6b7280' }} />
            </button>
            
            {showMenu && (
              <div style={styles.menuDropdown}>
                <button
                  onClick={() => {
                    setShowRating(true);
                    setShowMenu(false);
                  }}
                  style={styles.menuItem}
                  onMouseOver={(e) => e.target.style.background = '#f9fafb'}
                  onMouseOut={(e) => e.target.style.background = 'white'}
                >
                  <Check size={16} /> Finalizado
                </button>
                <button
                  onClick={() => {
                    handleChangeEstado('abandonado');
                    setShowMenu(false);
                  }}
                  style={{ ...styles.menuItem, color: '#ea580c' }}
                  onMouseOver={(e) => e.target.style.background = '#f9fafb'}
                  onMouseOut={(e) => e.target.style.background = 'white'}
                >
                  <Trash2 size={16} /> Abandonar
                </button>
              </div>
            )}
          </div>
        )}
      </div>

        <a 
          href={`https://www.google.com/search?q=${encodeURIComponent(anime.nombre + ' anime')}`}
          target="_blank"
          rel="noopener noreferrer"
          style={styles.searchLink}
          onMouseOver={(e) => e.target.style.textDecoration = 'underline'}
          onMouseOut={(e) => e.target.style.textDecoration = 'none'}
        >
          üîç Buscar en Google
        </a>

      {isWatching ? (
        <div style={styles.capitulosContainer}>
          <span style={styles.capitulosLabel}>Cap√≠tulos:</span>
          <div style={styles.capitulosControls}>
            <button 
              onClick={() => handleUpdateCapitulos(-1)}
              style={styles.capitulosButton}
              onMouseOver={(e) => e.target.style.background = '#e5e7eb'}
              onMouseOut={(e) => e.target.style.background = 'transparent'}
            >
              <ChevronDown size={18} />
            </button>
            <span style={styles.capitulosNumber}>{anime.capitulos_vistos}</span>
            <button 
              onClick={() => handleUpdateCapitulos(1)}
              style={styles.capitulosButton}
              onMouseOver={(e) => e.target.style.background = '#e5e7eb'}
              onMouseOut={(e) => e.target.style.background = 'transparent'}
            >
              <ChevronUp size={18} />
            </button>
          </div>
        </div>
      ) : (
        <div style={styles.infoContainer}>
          <div style={styles.infoRow}>
            <span style={styles.infoLabel}>Cap√≠tulos vistos:</span>
            <span style={styles.infoValue}>{anime.capitulos_vistos}</span>
          </div>
          
          {anime.estado === 'completado' && anime.calificacion > 0 && (
            <div style={styles.ratingContainer}>
              <span style={styles.infoLabel}>Calificaci√≥n:</span>
              <div style={styles.starContainer}>
                {[1,2,3,4,5].map(n => (
                  <Star 
                    key={n} 
                    size={16} 
                    className={n <= anime.calificacion ? 'fill-yellow-400 text-yellow-400' : 'text-gray-300'}
                    style={{ 
                      fill: n <= anime.calificacion ? '#facc15' : 'none',
                      color: n <= anime.calificacion ? '#facc15' : '#d1d5db'
                    }}
                  />
                ))}
              </div>
            </div>
          )}

          <div style={styles.buttonGroup}>
            {anime.estado === 'completado' && (
              <button
                onClick={() => handleChangeEstado('viendo', 0)}
                style={{ ...styles.actionButton, flex: 1, background: '#22c55e', color: 'white' }}
                onMouseOver={(e) => e.target.style.background = '#16a34a'}
                onMouseOut={(e) => e.target.style.background = '#22c55e'}
              >
                <PlayCircle size={16} /> Nueva Temporada
              </button>
            )}
            {anime.estado === 'abandonado' && (
              <button
                onClick={() => handleChangeEstado('viendo')}
                style={{ ...styles.actionButton, flex: 1, background: '#3b82f6', color: 'white' }}
                onMouseOver={(e) => e.target.style.background = '#2563eb'}
                onMouseOut={(e) => e.target.style.background = '#3b82f6'}
              >
                <PlayCircle size={16} /> Retomar
              </button>
            )}
            <button
              onClick={() => onDelete(anime.id)}
              style={{ ...styles.actionButton, background: '#ef4444', color: 'white' }}
              onMouseOver={(e) => e.target.style.background = '#dc2626'}
              onMouseOut={(e) => e.target.style.background = '#ef4444'}
            >
              <Trash2 size={16} /> Eliminar
            </button>
          </div>
        </div>
      )}

      {showRating && (
        <div style={styles.overlay}>
          <div style={styles.modal}>
            <h3 style={styles.modalTitle}>Califica "{anime.nombre}"</h3>
            <div style={styles.starsContainer}>
              {[1,2,3,4,5].map(n => (
                <button
                  key={n}
                  onClick={() => setRating(n)}
                  style={styles.starButton}
                  onMouseOver={(e) => e.currentTarget.style.transform = 'scale(1.1)'}
                  onMouseOut={(e) => e.currentTarget.style.transform = 'scale(1)'}
                >
                  <Star 
                    size={32} 
                    style={{ 
                      fill: n <= rating ? '#facc15' : 'none',
                      color: n <= rating ? '#facc15' : '#d1d5db'
                    }}
                  />
                </button>
              ))}
            </div>
            <div style={styles.modalButtons}>
              <button
                onClick={() => setShowRating(false)}
                style={{ ...styles.actionButton, flex: 1, background: '#e5e7eb', color: '#1f2937' }}
                onMouseOver={(e) => e.target.style.background = '#d1d5db'}
                onMouseOut={(e) => e.target.style.background = '#e5e7eb'}
              >
                Cancelar
              </button>
              <button
                onClick={handleFinish}
                style={{ ...styles.actionButton, flex: 1, background: '#9333ea', color: 'white' }}
                onMouseOver={(e) => e.target.style.background = '#7c3aed'}
                onMouseOut={(e) => e.target.style.background = '#9333ea'}
              >
                Finalizar
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default AnimeCard;
